import { IBaseFilter } from "@shared/interfaces"

//base filter query utils
export const concatFilterQuery = (options: IBaseFilter): string => {
	return `searchTerm=${options.searchTerm || ""}&page=${
		options.page || ""
	}&take=${options.take || ""}&between=${options.between || ""}&before=${
		options.before || ""
	}&after=${options.after || ""}&fetchAll=${options.fetchAll || ""}&single=${
		options.single || ""
	}&relations=${options.relations || ""}&selects=${options.selects || ""}`
}

const storagePrefix = "jd_react_"

export const storage = {
	getToken: () => {
		return JSON.parse(
			window.localStorage.getItem(`${storagePrefix}token`) as string
		)
	},
	setToken: (token: string) => {
		window.localStorage.setItem(`${storagePrefix}token`, JSON.stringify(token))
	},
	clearToken: () => {
		window.localStorage.removeItem(`${storagePrefix}token`)
	},

	storageSet(data: any, key: string) {
		if (!window.localStorage || !window.JSON || !key) {
			return
		}
		localStorage.setItem(key, JSON.stringify(data))
	},
	lsGetData(key: string) {
		if (!window.localStorage || !window.JSON || !key) {
			return
		}
		var item = localStorage.getItem(key)

		if (!item) {
			return
		}

		return JSON.parse(item)
	},

	lsRemoveData(key: string) {
		if (!window.localStorage || !window.JSON || !key) {
			return
		}
		localStorage.removeItem(key)
	},
}
