import { Button, Space, Table } from "antd"

import { IRole } from "@shared/interfaces"
import { Link } from "react-router-dom"
import { PathEnum } from "@shared/enums"
import { useDeleteRole } from "../hooks/useDeleteRole"
import { useRoles } from "../hooks/useRoles"
import { useState } from "react"

const UserRole = () => {
	const deleteRole = useDeleteRole({})

	const [page, setPage] = useState<number>(1)
	const { data, isLoading } = useRoles({
		options: {
			page: page,
			take: 10,
		},
	})

	const dataSource = data?.data?.payload?.map((x: IRole) => ({
		title: x.title,
		id: x.id,
		isActive: x.isActive ? "True" : "False",
	}))

	const columns = [
		{
			title: "Role Title",
			dataIndex: "title",
			key: "title",
		},
		{
			title: "Is Active",
			dataIndex: "isActive",
			key: "isActive",
		},
		{
			title: "Action",
			key: "id",
			render: (record: any) => (
				<Space size="middle">
					<Link to={`${PathEnum.UserUpdate}/${record.id}`}>Update</Link>
					<Button
						type="primary"
						danger
						onClick={() => deleteRole.mutateAsync(record.id)}>
						Delete
					</Button>
				</Space>
			),
		},
	]

	return (
		<Table
			columns={columns}
			dataSource={dataSource}
			loading={isLoading}
			pagination={{
				pageSize: 10,
				total: data?.data?.total,
				onChange: (page: number) => {
					setPage(page)
				},
			}}
		/>
	)
}

export default UserRole
