import { CoreAxiosInstance } from "./../config/axios/core-axios-instantance"
import { IAuthCredential } from "./../interfaces/auth.interfaces"

class _AuthService {
	END_POINT = "/auth"
	login(payload: IAuthCredential) {
		return CoreAxiosInstance.post(`${this.END_POINT}`, payload)
	}
}

export const AuthService = new _AuthService()
