import { Authorization } from '@modules/auth';
import { AxiosResponse } from 'axios';
import CreateRoleForm from '../components/CreateRoleForm';
import { PageHeader } from 'antd';
import { PathEnum } from '@shared/enums';
import { useCreateRole } from '../hooks/useCreateRole';
import { useNavigate } from 'react-router-dom';

const RoleCreatePage = () => {

  const navigate = useNavigate();

  const CreateRoleMutation = useCreateRole({
    config: {
      onSuccess: (data: AxiosResponse) => {
        navigate(PathEnum.RolesList);
      },
    },
  });


  interface IValues {
    title: string
    isActive: boolean
  }

  const onFinish = (values: IValues) => {
    CreateRoleMutation.mutateAsync(values)
  }


  return (
    <Authorization allowedAccess={["RoleCreate"]}>
      <PageHeader onBack={() => null} title="Create Role" />
      <CreateRoleForm
        isLoading={CreateRoleMutation.isLoading}
        onFinish={onFinish}
      />
    </Authorization>
  );
};

export default RoleCreatePage;
