import { useLocation, useNavigate } from "react-router-dom";

import EditBannerForm from "./EditBannerForm";
import Modal from "antd/lib/modal/Modal";
import { Pagination } from 'antd';
import { useAllBanner } from "../hooks/useAllBanner";
import { useState } from "react";

const BannerData = [
    {
        id: 1,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
    {
        id: 2,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
    {
        id: 3,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
    {
        id: 4,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
    {
        id: 5,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
    {
        id: 6,
        title: "Title1",
        bannerUrl: "https://api.airmoll.com/1626517864552.jpg",
        bannerType: "top"
    },
]

const BannerList = () => {

    const navigate = useNavigate()
    const { search } = useLocation() || false;

    const [page, setPage] = useState<number>(1)

    const { data, isLoading } = useAllBanner({
        options: {
            page: page,
            take: 6,
        },
    })

    const handleOk = () => {
        navigate('')
    };

    const handleCancel = () => {
        navigate('');
    };

    console.log(page);


    return (
        <div className="banner-list">
            <div className="grid grid-cols-3 gap-6">
                {
                    BannerData.map(banner =>
                        <div key={banner.id} className="banner-card">
                            <div className="banner-image">
                                <img src={banner.bannerUrl} alt="" />
                            </div>

                            <div className="banner-details">
                                <h2 className="title">{banner.title}</h2>
                                <div className="action">
                                    <button onClick={() => navigate(`?edit=${banner.id}`)} >Edit</button>
                                    <button>Details</button>
                                    <button>Remove</button>
                                </div>
                            </div>
                        </div>
                    )
                }
            </div>

            <div className="list-pagination">
                <Pagination
                    pageSize={10}
                    total={50}
                    onChange={(page) => setPage(page)}
                />
            </div>

            <Modal
                centered
                title="Update Banner"
                width={700}
                visible={search.startsWith("?edit") ? true : false}
                onOk={handleOk}
                okText="Update"
                onCancel={handleCancel}
            >
                <EditBannerForm />
            </Modal>
        </div>
    );
};

export default BannerList;
