import { Authorization } from '@modules/auth';
import { AxiosResponse } from 'axios';
import CreatePermissionTypeForm from '../components/CreatePermissionTypeForm';
import { PageHeader } from 'antd';
import { PathEnum } from '@shared/enums';
import { useCreatePermissionType } from '../hooks/useCreatePermissionType';
import { useNavigate } from 'react-router-dom';

const PermissionTypeCreatePage = () => {

  const navigate = useNavigate();

  const CreatePermissionTypeMutation = useCreatePermissionType({
    config: {
      onSuccess: (data: AxiosResponse) => {
        navigate(PathEnum.PermissionsTypeList);
      },
    },
  });


  interface IValues {
    title: string
    isActive: boolean
  }

  const onFinish = (values: IValues) => {
    CreatePermissionTypeMutation.mutateAsync(values)
  }


  return (
    <Authorization allowedAccess={["PermissionTypeCreate"]}>
      <PageHeader onBack={() => null} title="Create Permission Type" />
      <CreatePermissionTypeForm
        isLoading={CreatePermissionTypeMutation.isLoading}
        onFinish={onFinish}
      />
    </Authorization>
  );
};

export default PermissionTypeCreatePage;
