import { ENV } from "./../../../ENV"
import axios from "axios"
import { storage } from "@shared/utils"
const headers = {
	"Content-Type": "application/json",
	"X-Request-ID": 124,
	"X-Country-Code": "BD",
	"X-Client-Name": "CRM",
	"X-Client-Version": 123,
	Authorization: `Bearer ${storage?.getToken()}`,
}
export const CoreAxiosInstance = axios.create({
	baseURL: ENV.CORE_END_POINT,
	timeout: 60000,
	headers,
})
CoreAxiosInstance.interceptors.request.use(
	(config: any) => {
		config.headers["Authorization"] = `Bearer ${storage?.getToken()}`
		return config
	},
	(error: any) => {
		return Promise.reject(error)
	}
)
CoreAxiosInstance.interceptors.response.use(
	(response: any) => {
		return response?.data
	},
	(error: any) => {
		if (error?.response?.status === 401) {
			// store.dispatch(authLogoutAction())
		} else {
			return error
		}
	}
)
