import { Button, Col, Form, Input, Row, Select, Upload, message } from "antd"
import { LoadingOutlined, PlusOutlined } from "@ant-design/icons"

import UploadOutlined from "@ant-design/icons/lib/icons/UploadOutlined"
import { useState } from "react"

const { Option } = Select

const CreateBannerForm = () => {
	const onFinish = (values: object) => {
		console.log(values)
	}

	const onSearch = (value: any) => {
		console.log(value)
	}

	function getBase64(img: any, callback: any) {
		const reader = new FileReader()
		reader.addEventListener("load", () => callback(reader.result))
		reader.readAsDataURL(img)
	}

	function beforeUpload(file: any) {
		const isJpgOrPng = file.type === "image/jpeg" || file.type === "image/png"
		if (!isJpgOrPng) {
			message.error("You can only upload JPG/PNG file!")
		}
		const isLt2M = file.size / 1024 / 1024 < 2
		if (!isLt2M) {
			message.error("Image must smaller than 2MB!")
		}
		return isJpgOrPng && isLt2M
	}

	const [image, setImage]: any = useState({
		loading: false,
		imageUrl: null,
	})

	const handleChange = (info: any) => {
		if (info.file.status === "uploading") {
			setImage({ loading: true })
			return
		}
		if (info.file.status === "done") {
			// Get this url from response in real world.
			getBase64(info.file.originFileObj, (imageUrl: any) =>
				setImage({
					imageUrl,
					loading: false,
				})
			)
		}
	}

	const { loading, imageUrl } = image
	const uploadButton = (
		<div>
			{loading ? <LoadingOutlined /> : <PlusOutlined />}
			<div style={{ marginTop: 8 }}>Upload Image</div>
		</div>
	)

	console.log(imageUrl)
	const normFile = (e: any) => {
		console.log("Upload event:", e)
		if (Array.isArray(e)) {
			return e
		}
		return e && e.fileList
	}
	return (
		<div className="banner-create-form">
			<Row gutter={{ xs: 10, sm: 16, md: 24, lg: 32 }} align="middle">
				{/* <Col span={10}>
					<Upload
						name="image"
						listType="picture-card"
						className="avatar-uploader"
						showUploadList={false}
						action="https://mighty-mickey.poshapets.com/api/v1/images/upload"
						beforeUpload={beforeUpload}
						onChange={handleChange}></Upload>
				</Col> */}

				<Col span={14}>
					<Form size="large" layout="vertical" onFinish={onFinish}>
						<Form.Item
							name="image"
							label="Upload"
							valuePropName="fileList"
							getValueFromEvent={normFile}
							extra="longgggggggggggggggggggggggggggggggggg">
							<Upload
								name="logo"
								action="https://mighty-mickey.poshapets.com/api/v1/images/upload"
								listType="picture">
								<Button icon={<UploadOutlined />}>Click to upload</Button>
							</Upload>
						</Form.Item>
						<Form.Item
							label="Title"
							name="title"
							rules={[{ required: true, message: "Please input a title!" }]}>
							<Input placeholder="Banner title"></Input>
						</Form.Item>

						<Form.Item
							initialValue={imageUrl ? imageUrl : null}
							label="Banner Url"
							name="bannerUrl"
							rules={[{ required: true, message: "Please input a title!" }]}>
							<Input value={imageUrl} placeholder="Banner Ulr"></Input>
						</Form.Item>

						<Form.Item
							label="Banner Type"
							name="bannerType"
							rules={[{ required: true, message: "Please input a title!" }]}>
							<Select
								showSearch
								placeholder="Select a type"
								optionFilterProp="children"
								onSearch={onSearch}
								filterOption={(input, option: any) =>
									option.children.toLowerCase().indexOf(input.toLowerCase()) >=
									0
								}>
								<Option value="jack">Jack</Option>
								<Option value="lucy">Lucy</Option>
								<Option value="tom">Tom</Option>
							</Select>
						</Form.Item>
					</Form>
				</Col>
			</Row>
		</div>
	)
}

export default CreateBannerForm
