import { CoreAxiosInstance } from "@shared/config/axios/core-axios-instantance"
import { IBaseFilter } from "@shared/interfaces"
import { concatFilterQuery } from "@shared/utils"

export const baseService = (endPoint: string): any => ({
	filter(options: IBaseFilter) {
		return CoreAxiosInstance.get(`${endPoint}?${concatFilterQuery(options)}`)
	},
	getById(id: string) {
		return CoreAxiosInstance.get(endPoint + id)
	},

	create(payload: any) {
		return CoreAxiosInstance.post(endPoint, payload)
	},

	update(id: string, payload: any) {
		return CoreAxiosInstance.put(endPoint + id, payload)
	},
	delete(id: string) {
		return CoreAxiosInstance.delete(endPoint + id)
	},
})

export const BaseService = {
	END_POINT: "",
	filter(options: IBaseFilter) {
		return CoreAxiosInstance.get(
			`${BaseService.END_POINT}?${concatFilterQuery(options)}`
		)
	},
	getById(id: string) {
		return CoreAxiosInstance.get(this.END_POINT + id)
	},

	create(payload: any) {
		return CoreAxiosInstance.post(this.END_POINT, payload)
	},

	update(id: string, payload: any) {
		return CoreAxiosInstance.put(this.END_POINT + id, payload)
	},
	delete(id: string) {
		return CoreAxiosInstance.delete(this.END_POINT + id)
	},
}
