import { PageHeader, Spin } from "antd";
import { useNavigate, useParams } from "react-router-dom";

import { Authorization } from "@modules/auth";
import { AxiosResponse } from "axios";
import { PathEnum } from "@shared/enums";
import UpdateRoleForm from "../components/UpdateRoleForm";
import { useRole } from "../hooks/useRole";
import { useUpdateRole } from "../hooks/useUpdateRole";

export const RoleUpdatePage = () => {

  const navigate = useNavigate();
  const { id } = useParams();
  const {data, isLoading} = useRole({id});

  const updateRoleMutation = useUpdateRole({
    config: {
      onSuccess: (data: AxiosResponse) => {
        navigate(PathEnum.RolesList);
      },
    },
  });


  return isLoading ? <Spin /> : (
    <Authorization allowedAccess={["UserModify"]}>
      {" "}
      <PageHeader onBack={() => null} title="Update User" />
      <UpdateRoleForm
        initialValues={{
          title: data?.data?.payload?.title,
          isActive: data?.data?.payload?.isActive

        }}
        isLoading={updateRoleMutation.isLoading}
        onFinish={(values) => updateRoleMutation.mutateAsync({...values, id})}
      />
    </Authorization>
  );
};
