import { PageHeader, Spin } from "antd"
import { useNavigate, useParams } from "react-router-dom"

import { Authorization } from "@modules/auth"
import { AxiosResponse } from "axios"
import { Paths } from "@shared/enums"
import UpdatePermissionTypeForm from "../components/elements/UpdatePermissionTypeForm"
import { usePermissionType } from "../hooks/usePermissionType"
import { useUpdatePermissionType } from "../hooks/useUpdatePermissionType"

const PermissionTypeUpdatePage = () => {
	const navigate = useNavigate()
	const { id } = useParams()
	const { data, isLoading } = usePermissionType({ id })

	const updatePermissionTypeMutation = useUpdatePermissionType({
		config: {
			onSuccess: (data: AxiosResponse) => {
				navigate(Paths.PermissionsTypeList)
			},
		},
	})

	interface IValues {
		title: string
		isActive: boolean
	}

	const onFinish = (values: IValues) => {
		updatePermissionTypeMutation.mutateAsync({ ...values, id })
	}

	return isLoading ? (
		<Spin />
	) : (
		<Authorization allowedAccess={["RoleModify"]}>
			<PageHeader onBack={() => null} title="Update Role" />
			<UpdatePermissionTypeForm
				initialValues={{
					title: data?.data?.payload?.title,
					isActive: data?.data?.payload?.isActive,
				}}
				isLoading={updatePermissionTypeMutation.isLoading}
				onFinish={onFinish}
			/>
		</Authorization>
	)
}

export default PermissionTypeUpdatePage
