import { CoreAxiosInstance } from "./../config/axios/core-axios-instantance"
import { IAuthCredential } from "./../interfaces/auth.interfaces"

class _AuthService {
	public END_POINT = "/auth/"
	constructor(endPoint: string) {
		this.END_POINT = endPoint
	}
	async login(payload: IAuthCredential) {
		console.log(this.END_POINT, "I am called")
		return await CoreAxiosInstance.post(`${this.END_POINT}`, payload)
	}
}

export const AuthService = new _AuthService("/auth/")
