import {
	Avatar,
	Comment,
	Dropdown,
	Layout,
	Menu,
	Space,
	Tag,
	Typography,
} from "antd"
import {
	DashboardOutlined,
	DownOutlined,
	MenuFoldOutlined,
	MenuUnfoldOutlined,
	PoweroffOutlined,
	UserOutlined,
} from "@ant-design/icons"
import React, { useState } from "react"

import { IMAGES } from "@shared/assets"
import { PathEnum } from "@shared/enums"
import { useNavigate } from "react-router-dom"
import { useResponsive } from "ahooks"

const { Header, Sider, Content } = Layout
const { Title, Text } = Typography
interface IFProps {
	children: any
}
const MainLayout: React.FC<IFProps> = ({ children }) => {
	const navigate = useNavigate()
	const { md } = useResponsive()
	const [isCollapsed, setIsCollapsed] = useState<boolean>(true)
	let pathname = window.location.pathname
	const menu = (
		<Menu>
			<Menu.Item>
				<DownOutlined /> My Profile
			</Menu.Item>
			<Menu.Item>
				<DownOutlined /> Preferences
			</Menu.Item>
		</Menu>
	)

	const styles = {
		sider: {
			background: "#fff",
			boxShadow: "0 0 20px #0815420d",
			borderRight: "1px solid #ecf3fa",
			overflow: "auto",
			height: "100vh",
			position: "fixed",
			left: !md && isCollapsed ? "-100%" : 0,
			zIndex: 9,
		},
		layout: {
			background: "#f6f8fa",
			marginLeft: !md ? 0 : isCollapsed ? 80 : 200,
		},

		header: {
			position: "fixed",
			width: "100%",
			background: "#fff",
			padding: "0 14px",
			display: "flex",
			alignItems: "center",
			justifyContent: "space-between",
			zIndex: 8,
			paddingLeft: !md ? (isCollapsed ? 20 : 220) : isCollapsed ? 100 : 220,
			right: 0,
			boxShadow: "0 0 20px #0815420d",
			borderBottom: "1px solid #ecf3fa",
		},
		content: {
			borderRadius: 5,
			padding: 14,
			minHeight: 280,
			background: "#f6f8fa",
			marginTop: 63,
			marginLeft: 0,
			marginRight: 14,
		},
	}
	return (
		<>
			<Layout style={{ minHeight: "100vh" }}>
				<Sider
					style={styles.sider as any}
					breakpoint="md"
					onBreakpoint={(broken) => {
						if (broken === true) {
							setIsCollapsed(true)
						}
					}}
					trigger={null}
					collapsible
					collapsed={isCollapsed}>
					<div
						style={{ margin: 15, display: "flex", justifyContent: "center" }}>
						{isCollapsed ? (
							<img
								className="logo"
								src={IMAGES.LogoSmall}
								style={{ width: 30 }}
								alt="logo"
							/>
						) : (
							<img
								className="logo"
								src={IMAGES.Logo}
								style={{ width: 110 }}
								alt="logo"
							/>
						)}
					</div>
					<div style={{ padding: 5, marginTop: 30 }}>
						{isCollapsed ? (
							<div style={{ display: "flex", justifyContent: "center" }}>
								<Space direction="vertical" style={{ textAlign: "center" }}>
									<Avatar src={`https://i.ibb.co/LNCj9Fd/mostbeautiful.jpg`} />
									<Dropdown overlay={menu}>
										<DownOutlined style={{ fontSize: 10 }} />
									</Dropdown>
								</Space>
							</div>
						) : (
							<Comment
								author={<Text strong>Welcome,</Text>}
								avatar={
									<Avatar src={`https://i.ibb.co/LNCj9Fd/mostbeautiful.jpg`} />
								}
								content={
									<Dropdown overlay={menu}>
										<Space>
											<Title
												style={{ fontSize: 14, textTransform: "capitalize" }}
												level={5}>
												Econ
												<DownOutlined style={{ fontSize: 10 }} />
											</Title>
										</Space>
									</Dropdown>
								}
							/>
						)}
					</div>

					<Menu
						theme="light"
						mode="inline"
						defaultSelectedKeys={[String(pathname)]}
						defaultOpenKeys={[String(pathname)]}>
						<Menu.Item
							icon={<DashboardOutlined />}
							onClick={() => navigate(`${PathEnum.Dashboard}`)}
							key={String(`${PathEnum.Dashboard}`)}>
							Analytics
						</Menu.Item>
						<SubMenu key={PathEnum.Users} icon={<UserOutlined />} title="User">
							<Menu.Item
								onClick={() => navigate(PathEnum.UserCreate)}
								key={PathEnum.UserCreate}>
								Create
							</Menu.Item>
							<Menu.Item onClick={() => navigate(PathEnum.UserList)} key="4">
								List
							</Menu.Item>
						</SubMenu>
					</Menu>
				</Sider>
				<Layout style={styles.layout as any}>
					<Header style={styles.header as any}>
						<div
							style={{ fontSize: 22, cursor: "pointer" }}
							onClick={() => setIsCollapsed(!isCollapsed)}>
							{isCollapsed ? <MenuUnfoldOutlined /> : <MenuFoldOutlined />}
						</div>

						<Tag
							onClick={() => {
								navigate(PathEnum.root)
							}}
							style={{ cursor: "pointer" }}
							icon={<PoweroffOutlined />}
							color="default">
							Logout
						</Tag>
					</Header>
					<Content style={styles.content as any}>{children}</Content>
				</Layout>
			</Layout>
		</>
	)
}

export default MainLayout
const { SubMenu } = Menu
