import { IBaseFilter } from "@shared/interfaces"
import { concatFilterQuery } from "@shared/utils"

export class BaseService<ICreatUser, IUpdateUser> {
	END_POINT: string | undefined
	httpClient: any

	constructor(private http: any) {
		this.httpClient = http
	}
	filter(options: IBaseFilter) {
		console.log(this.END_POINT)
		return this.httpClient.get(
			`${this.END_POINT}?${concatFilterQuery(options)}`
		)
	}
	getById(id: string) {
		return this.httpClient.get(this.END_POINT + id)
	}

	create(payload: ICreatUser) {
		return this.httpClient.post(this.END_POINT, payload)
	}

	update(id: string, payload: IUpdateUser) {
		return this.httpClient.put(this.END_POINT + id, payload)
	}

	delete(id: string) {
		return this.httpClient.delete(this.END_POINT + id)
	}
}
