import { PageHeader, Spin } from "antd"
import { useNavigate, useParams } from "react-router-dom"

import { Authorization } from "@modules/auth"
import { AxiosResponse } from "axios"
import { PathEnum } from "@shared/enums"
import UpdateRoleForm from "../../permission/components/UpdateRoleForm"
import { useRole } from "@modules/permission/hooks/useRole"
import { useUpdateRole } from "@modules/permission/hooks/useUpdateRole"

const RoleUpdatePage = () => {
	const navigate = useNavigate()
	const { id } = useParams()
	const { data, isLoading } = useRole({ id })

	const updateRoleMutation = useUpdateRole({
		config: {
			onSuccess: (data: AxiosResponse) => {
				navigate(PathEnum.RolesList)
			},
		},
	})

	interface IValues {
		title: string
		isActive: boolean
	}

	const onFinish = (values: IValues) => {
		updateRoleMutation.mutateAsync({ ...values, id })
	}

	return isLoading ? (
		<Spin />
	) : (
		<Authorization allowedAccess={["RoleModify"]}>
			<PageHeader onBack={() => null} title="Update Role" />
			<UpdateRoleForm
				initialValues={{
					title: data?.data?.payload?.title,
					isActive: data?.data?.payload?.isActive,
				}}
				isLoading={updateRoleMutation.isLoading}
				onFinish={onFinish}
			/>
		</Authorization>
	)
}

export default RoleUpdatePage
