import { Col, Form, Input, Row, Select } from 'antd';

const { Option } = Select;


const EditBannerForm = () => {

    const onFinish = (values: object) => {
        console.log(values);
    }

    const onSearch = (value: any) => {
        console.log(value);
    }

    return (
        <div className="banner-create-form">
            <Row gutter={{ xs: 10, sm: 16, md: 24, lg: 32 }} align="middle">
                <Col span={10}>
                    <img src="https://i.ibb.co/ZWKWR4G/school-vector.jpg" alt="avatar" />
                </Col>

                <Col span={14}>
                    <Form
                        size="large"
                        layout="vertical"
                        onFinish={onFinish}
                    >
                        <Form.Item
                            label="Title"
                            name="title"
                            rules={[{ required: true, message: 'Please input a title!' }]}
                        >
                            <Input placeholder="Banner title"></Input>
                        </Form.Item>

                        <Form.Item
                            label="Banner Url"
                            name="bannerUrl"
                            rules={[{ required: true, message: 'Please input a title!' }]}
                        >
                            <Input placeholder="Banner Ulr"></Input>
                        </Form.Item>

                        <Form.Item
                            label="Banner Type"
                            name="bannerType"
                            rules={[{ required: true, message: 'Please input a title!' }]}
                        >
                            <Select
                                showSearch
                                placeholder="Select a type"
                                optionFilterProp="children"
                                onSearch={onSearch}
                                filterOption={(input, option: any) =>
                                    option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                }
                            >
                                <Option value="jack">Jack</Option>
                                <Option value="lucy">Lucy</Option>
                                <Option value="tom">Tom</Option>
                            </Select>
                        </Form.Item>
                    </Form>
                </Col>
            </Row>
        </div>
    );
};

export default EditBannerForm;
