import { Button, PageHeader } from "antd";
import { useLocation, useNavigate } from "react-router-dom";

import { Authorization } from "@modules/auth";
import BannerList from "../components/BannerList";
import CreateBannerForm from "../components/CreateBannerForm";
import Modal from "antd/lib/modal/Modal";

const BannersPage = () => {

    const navigate = useNavigate()
    const {search} = useLocation() || false;

    const handleOk = () => {
        navigate('')
    };

    const handleCancel = () => {
        navigate('');
    };



    return (
        <Authorization allowedAccess={["FORBIDDEN"]}>
            <PageHeader
                onBack={() => null}
                title="Banners"
                extra={[
                    <Button onClick={()=> navigate("?createBanner")} type="primary">
                        Create Banner
                    </Button>
                ]}
            />

            <BannerList />

            <Modal
                centered
                title="Create Banner"
                width={700}
                visible={search.startsWith("?createBanner") ? true : false}
                onOk={handleOk}
                okText="Update"
                onCancel={handleCancel}
            >
                <CreateBannerForm />
            </Modal>
        </Authorization>
    );
};

export default BannersPage;
